import React, { useState } from 'react';
import axios from '../api/axios';
import Modal from '../Components/Modal';
import { useForm } from 'react-hook-form';
import {useCookies} from 'react-cookie';

const TICKET_URL = '/ticket';

const Home = () => {
    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');
    const [getTicketNumber, setGetTicketNumber] = useState(0);
    const [cookies, setCookies] = useCookies(["name", "lastname", "ticketNumber"]);

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
        reset,
        getValues,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            name: "",
            lastname: "",
            ticketNumber: 0,
        }
    });
    
    const userToAdd = {name: data.name, lastname: data.lastname, ticketNumber: data.ticketNumber}
    const prevUsers = cookies["users"] || [];
    const newUsers = [...prevUsers, userToAdd];
    const cookieData = new Date();
    cookieData.setTime(cookieData.getTime() + ( 60 * 10000));

    const onSubmit = async (data) => {
        

        await axios.post(TICKET_URL, data)
            .then((res) => {

                setCookies("users", newUsers, {path: "/", expires: cookieData});

                setPostMessage(res.data.success);
                setShowModal(true);

            })
            .catch((err)=>{console.log(err)})
    };

    const handleClose = () => {
        setShowModal(false)
        reset({name: "", lastname: "", ticketNumber: 0});
    }


    return(
        <>
            <Modal showModal={showModal} postMessage={postMessage} ticketNumber={getTicketNumber} handleClose={handleClose}/>
            <div className='container d-flex justify-content-center'>
                <div className="card text-center">
                    <div className="card-header">
                        Loterijos bilietas
                    </div>
                    <div className='card-body'>
                        <form onSubmit={handleSubmit(onSubmit)} method="POST">
                            <div className='form-group'>
                                <input 
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Įveskite savo vardą'
                                    {...register("name", {required: true})}
                                />
                                <div class="invalid-feedback">
                                    {errors.name?.type === "required" && "Vardas yra privalomas"}
                                </div>
                            </div>
                            <div className='form-group'>
                                <input 
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Įveskite savo pavardę'
                                    {...register("lastname", {required: true})}
                                />
                                <div class="invalid-feedback">
                                    {errors.lastname?.type === "required" && "Pavardė yra privaloma"}
                                </div>
                            </div>
                            <div className='form-group'>
                                <input 
                                    type='number' 
                                    className='form-control' 
                                    placeholder='Įveskite norima skaičių' 
                                    {...register("ticketNumber", {min: 1, max: 1000})}
                                />
                                <div class="invalid-feedback">
                                    {errors.ticketNumber?.type === 'min' && "Minimalus bilieto skaičius 1"}
                                    {errors.ticketNumber?.type === 'max' && "Maksimalus bilieto skaičius 1000"}
                                </div>
                            </div>
                            <button 
                                type='submit' 
                                className='btn btn-primary'
                                onClick={() => {
                                    const values = getValues('ticketNumber');
                                    setGetTicketNumber(values);
                                }}
                            >
                                Nusipirkti
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
