import React, { useState, useEffect } from 'react';
import {useCookies} from 'react-cookie';
import axios from 'axios';

const TICKET_URL = 'http://localhost:8000/api/ticket-list';

const Profile = () => {
    const [cookies, setCookies] = useCookies(['users']);
    const [tickets, setTickets] = useState({});

    const getToken = localStorage.getItem('userToken');

    useEffect(() => {
        const getTickets = async () => {
            if(getToken)
            {
                const {data: res} = await axios.get(`http://localhost:8000/api/ticket-list/${getToken}`)
                setTickets(res);
            }
        }
        getTickets();
    }, [])

    console.log(tickets.tickets);
 
    return(
        <>
            <div className='container d-flex profile justify-content-center'>
                {tickets.tickets?.map((ticket) => (
                    <>
                        {cookies.users?.map((user) => (
                            <>
                                {ticket.token === user.token && (
                                    <>
                                        <div className="card card-ticket text-center">
                                            <div className="card-header">
                                                Loterijos bilietas #{ticket.ticketNumber}
                                            </div>
                                            <div className='card-body'>
                                                <p>{ticket.name}</p>
                                                <p>{ticket.lastname}</p>
                                            </div>
                                        </div>
                                    
                                    </>
                                )}
                            </>
                        ))}
                    </>
                ))}
            </div>
        </>
    )
}

export default Profile;
