import React, { useState, useEffect } from 'react';
import Card from '../Components/Card';
import axios from '../api/axios';
import SuccessMessage from '../Components/SuccessMessage';

const TICKET_URL = '/ticket';

const Home = () => {
    const [data, setData] = useState({
        name: "",
        lastname: "",
        ticketNumber: 0,
    });

    const [showNotification, setshowNotification] = useState(false);
    const [postMessage, setPostMessage] = useState('');

    useEffect(() => {
        const notificationTimer = setTimeout(() => {
            setshowNotification(true);
        }, 10000);

        return () => {
            clearTimeout(notificationTimer);
            setshowNotification(false);
        };
    }, []);

    const handleChange = (e) => {
        const {name, value} = e.target;
        setData({
            ...data,
            [name]: value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        await axios.post(TICKET_URL, data)
            .then((res) => {
                setPostMessage(res.data.success);
                console.log(res.data.success)
            })
            .catch((err)=>{console.log(err)})
    }


    return(
        <div className='container d-flex justify-content-center'>
            <div className="card text-center">
                <div className="card-header">
                    Loterijos bilietas
                </div>
                <div className='card-body'>
                <form onSubmit={handleSubmit} method="POST">
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo vardą'
                            name='name'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo pavardę'
                            name='lastname'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='number' 
                            className='form-control' 
                            placeholder='Įveskite norima skaičių' 
                            name='ticketNumber'
                            onChange={handleChange}
                        />
                    </div>
                    <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                    </form>
                </div>
            </div>
            {showNotification && postMessage && (
                <div className="h-full flex justify-center py-6">
                    <div className="flex max-w-xs w-full mt-4 mr-4 bg-white rounded shadow p-4">
                        <div className='mr-4'>
                            <img src="/images/success-message.png" alt="success" className="w-6 h-6 text-green-600"/>
                        </div>
                        <div className='flex-1 text-gray-800'>
                            {postMessage}
                        </div>
                    </div>
                    
                </div>
            )}
        </div>
    )
}

export default Home;
