import React, { useState, useEffect } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import axios from 'axios';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";



const Profile = (props) => {

    const getToken = localStorage.getItem('userToken');
    
    const TICKET_URL = `http://localhost:8000/api/ticket-list/${getToken}`;
    
    const [data, setData] = useState({});

    const { promiseInProgress } = usePromiseTracker({area: props.area});

    useEffect(() => {
        const getData = async () => {
            if(getToken)
            {
                const {data: res} = await axios.get(TICKET_URL)
                setData(res);
            }
        }
        trackPromise(getData());
    }, [])

    return(
        <>
            {promiseInProgress &&
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {!promiseInProgress &&
                <div className='container d-flex profile justify-content-center'>
                    {console.log(data.orders)}
                    {data.orders?.map((order) => (
                        <div className="card card-ticket text-center">
                        <div className="card-header">
                            Jusu orderis
                        </div>
                        <div className='card-body'>
                            <p>Nusipirkote: {order.quantity} bilietų</p>
                        </div>
                    </div>
                    ))}
                    {/* {tickets.tickets?.map((ticket) => (
                                <div className="card card-ticket text-center" key={ticket.id}>
                                    <div className="card-header">
                                        Loterijos bilietas #{ticket.ticketNumber}
                                    </div>
                                    <div className='card-body'>
                                        <p>{ticket.name}</p>
                                        <p>{ticket.lastname}</p>
                                    </div>
                                </div>
                    ))}
                    {tickets.tickets?.length === 0 && (
                            <div className="card card-ticket text-center">
                                <div className="card-header">
                                    Informacija
                                </div>
                                <div className='card-body'>
                                    <p>Šiuo metu neturite įsigyje loterijos bilieto</p>
                                </div>
                            </div>
                    )} */}
                </div>
            }
        </>
    )
}

export default Profile;
