    //     },
    //     {
    //       image: 'https://i.ibb.co/64k8D1c/good-5.png',
    //     },
    //   ];

      useEffect(() => {
        const getTickets = async () => {
            const {data: res} = await axios.get('http://localhost:8000/api/lottery-tickets');
            // console.log(res.tickets)
            setTickets(res.tickets);
        }
        getTickets();
      }, [])

    //   const reproductionArray = (array = [], length = 0) => [
    //     ...Array(length)
    //       .map(() => array[Math.floor(Math.random() * array.length)]),
    //   ];
      
    //   const reproducedPrizeList = [
    //     ...tickets,
    //     ...reproductionArray(tickets, tickets.length * 3),
    //     ...tickets,
    //     ...reproductionArray(tickets, tickets.length),
    //   ];
      
      const generateId = () =>
        `${Date.now().toString(36)}-${Math.random().toString(36).substring(2)}`;
      
      const prizeList = tickets.map((prize) => ({
        ...prize,
        id: generateId(),
      }));

    const prizeIndex = tickets.length * 4 + winPrizeIndex;
  
    const handleStart = () => {
      setStart((prevState) => !prevState);
    };
  
    const handlePrizeDefined = () => {
      console.log('ðŸ¥³ Prize defined! ðŸ¥³');
    };
    
    return(
        <>
        <div className='container d-flex admin-panel justify-content-center'>
                <RoulettePro
            prizes={prizeList}
            prizeIndex={prizeIndex}
            start={start}
            onPrizeDefined={handlePrizeDefined}
        />
        <button onClick={handleStart}>Start</button>

        </div>
        </>
    )
}

export default Other;
