import React, { useState } from 'react';
import axios from '../api/axios';
import Modal from '../Components/Modal';
import { useForm } from 'react-hook-form';
import {useCookies} from 'react-cookie';
import Checkbox from '../Components/Checkbox';

const TICKET_URL = '/ticket';

const Home = () => {
    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');
    const [childData, setChildData] = useState(null);
    const [list, setList] = useState({});
    const [cookies, setCookies] = useCookies(['users']);

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
        reset,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            name: "",
            lastname: "",
            ticketNumber: 0,
        }
    });

    // const randomNumberInRange = (min, max) => {
    //     const number = Math.floor(Math.random() * (max - min)+ min)
    //     return setList({...list, number});
    // }

    // const getRandomNumber = () => {
    //     for (let i = 0; i < 2; i++) {
    //         randomNumberInRange(1,9999)
    //     }
    // }
    const onSubmit = async (data) => {
        const cookieData = new Date();
        const userToAdd = {name: data.name, lastname: data.lastname};
        const prevUsers = cookies["users"] || [];
        // const newUsers = [];

    
        cookieData.setTime(cookieData.getTime() + ( 60 * 10000));
        // if(!isset(userToAdd)){
        //     const prevUsers = cookies["users"] || [];
        //     const newUsers = [...prevUsers, userToAdd];
        //     setCookies("users", newUsers, {path: "/", expires: cookieData})
        // }

        let i = 0;
        do {
            const number = Math.floor(Math.random() * (9999 - 1) + 1)
            data.ticketNumber = number;
            await axios.post(TICKET_URL, data)
            .then((res) => {
                // const prevUsers = cookies["users"] || [];
                // const newUsers = [...prevUsers, userToAdd];
                // if(!userToAdd.name === data.name || !userToAdd.lastname === data.lastname)
                // {
                //     const newUsers = [...prevUsers, userToAdd];
                // }
                if(!userToAdd.name === data.name || !userToAdd.lastname === data.lastname)
                {
                    const newUsers = [...prevUsers, userToAdd];
                    setCookies("users", newUsers, {path: "/", expires: cookieData});
                }
                setCookies("users", userToAdd, {path: "/", expires: cookieData});
                // setCookies("users", newUsers, {path: "/", expires: cookieData});
                setPostMessage(res.data.success);
                setShowModal(true);

                // console.log(childData)
                i = i + 1;
            })
            .catch((err)=>{console.log(err)})
        } while (i < 10);
        
        
            
        

        // console.log(list, " ", data);
        //     const userToAdd = {name: data.name, lastname: data.lastname, ticketNumber: data.ticketNumber};
        //     const prevUsers = cookies["users"] || [];
        //     const newUsers = [...prevUsers, userToAdd];
        //     const cookieData = new Date();
        //     cookieData.setTime(cookieData.getTime() + ( 60 * 10000));
        
        // await axios.post(TICKET_URL, data)
        //     .then((res) => {

        //         setCookies("users", newUsers, {path: "/", expires: cookieData});

        //         setPostMessage(res.data.success);
        //         setShowModal(true);

        //     })
        //     .catch((err)=>{console.log(err)})
        // }
    }

    const handleClose = () => {
        setShowModal(false)
        reset({name: "", lastname: "", ticketNumber: 0});
    }

    return(
        <>
            <Modal showModal={showModal} postMessage={postMessage}  handleClose={handleClose}/>
            <div className='container d-flex justify-content-center'>
                <div className="card text-center">
                    <div className="card-header">
                        Loterijos bilietas
                    </div>
                    <div className='card-body'>
                        <form onSubmit={handleSubmit(onSubmit)} method="POST">
                            <div className='form-group'>
                                <input 
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Įveskite savo vardą'
                                    {...register("name", {required: true})}
                                />
                                <div class="invalid-feedback">
                                    {errors.name?.type === "required" && "Vardas yra privalomas"}
                                </div>
                            </div>
                            <div className='form-group'>
                                <input 
                                    type='text' 
                                    className='form-control' 
                                    placeholder='Įveskite savo pavardę'
                                    {...register("lastname", {required: true})}
                                />
                                <div class="invalid-feedback">
                                    {errors.lastname?.type === "required" && "Pavardė yra privaloma"}
                                </div>
                            </div>
                            <p>Pasirinkite bilietų kiekį:</p>
                            <Checkbox labelText={'1 bilietas'} value={1} passChildData={setChildData}/>
                            <Checkbox labelText={'5 bilietas'} value={5} passChildData={setChildData}/>
                            <Checkbox labelText={'10 bilietas'} value={10} passChildData={setChildData}/>
                            <Checkbox labelText={'15 bilietas'} value={15} passChildData={setChildData}/>
                            <Checkbox labelText={'20 bilietas'} value={20} passChildData={setChildData}/>
                            <Checkbox labelText={'30 bilietas'} value={30} passChildData={setChildData}/>
                            <Checkbox labelText={'50 bilietas'} value={50} passChildData={setChildData}/>
                            <Checkbox labelText={'100 bilietas'} value={100} passChildData={setChildData}/>

                            <p>Pasirinkite mokėjimo būdą:</p>
                            <Checkbox labelText={'Grynais'} value={'cash'} passChildData={setChildData}/>
                            <Checkbox labelText={'Internetu'} value={'bank'} passChildData={setChildData}/>
                            <button 
                                type='submit' 
                                className='btn btn-primary'
                            >
                                Nusipirkti
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
