import React, { useState, useEffect } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import axios from 'axios';


const Profile = () => {
    const [loading, setLoading] = useState(false);

    const getToken = localStorage.getItem('userToken');
    
    const TICKET_URL = `http://localhost:8000/api/ticket-list/${getToken}`;
    
    const [tickets, setTickets] = useState({});


    useEffect(() => {
        const getTickets = async () => {
            if(getToken)
            {
                await axios.get(TICKET_URL)
                .then((res) => {
                    setTickets(res.data.tickets);
                    setLoading(true);
                })
                .catch((err) => {
                    if(err){

                        setLoading(true);
                    }
                })
            }else {
                setLoading(true);
            }
        }
        getTickets();
        setLoading(false);
    }, [])

    return(
        <>
            {!loading && 
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {loading && 
                <div className='container d-flex profile justify-content-center'>
                    {tickets?.length > 0 && tickets?.map((ticket) => (
                                <div className="card card-ticket text-center" key={ticket.id}>
                                    <div className="card-header">
                                        Loterijos bilietas #{ticket.ticketNumber}
                                    </div>
                                    <div className='card-body'>
                                        <p>{ticket.name}</p>
                                        <p>{ticket.lastname}</p>
                                    </div>
                                </div>
                    ))}
                    {tickets?.length < 1 && (
                            <div className="card card-ticket text-center">
                                <div className="card-header">
                                    Informacija
                                </div>
                                <div className='card-body'>
                                    <p>Šiuo metu neturite įsigyje loterijos bilieto</p>
                                </div>
                            </div>
                    )}
                </div>
            }
            {/* <Button loading loadingPosition="start" variant="outlined">
  Fetch data
</Button> */}
        </>
    )
}

export default Profile;
