import React, { useState } from 'react';

const Checkbox = ({labelText, value}) => {
    const [handleValue, setHandleValue] = useState(null);
    
    const handleClick = (value) => {
        setHandleValue(value);
    }
    
    return(
        <div className='form-check'>
            <input className='form-check-input' type={"radio"} name='flexRadio' value={value} onClick={() => handleClick(value)} />
            <label className='form-check-label' htmlFor='flexRadio1'>
                {labelText}
            </label>
        </div>
    )
}

export default Checkbox;
