import React, { useState, useEffect } from 'react';
import Card from '../Components/Card';
import axios from '../api/axios';
import SuccessMessage from '../Components/SuccessMessage';

const TICKET_URL = '/ticket';

const Home = () => {
    const [data, setData] = useState({
        name: "",
        lastname: "",
        ticketNumber: 0,
    });

    const [showModal, setShowModal] = useState(true);
    const [postMessage, setPostMessage] = useState('');

    const handleChange = (e) => {
        const {name, value} = e.target;
        setData({
            ...data,
            [name]: value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        await axios.post(TICKET_URL, data)
            .then((res) => {
                setPostMessage(res.data.success);
                setShowModal(true);
            })
            .catch((err)=>{console.log(err)})
    }

    const handleClose = () => {setShowModal(false)}

    return(
        <>
        <div className='container d-flex justify-content-center'>
            <div className="card text-center">
                <div className="card-header">
                    Loterijos bilietas
                </div>
                <div className='card-body'>
                <form onSubmit={handleSubmit} method="POST">
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo vardą'
                            name='name'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo pavardę'
                            name='lastname'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='number' 
                            className='form-control' 
                            placeholder='Įveskite norima skaičių' 
                            name='ticketNumber'
                            onChange={handleChange}
                        />
                    </div>
                    <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                    </form>
                </div>
            </div>
        </div>
            {showModal && (
                <div class="modal" tabindex="-1" role="dialog">
                    <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                        <h5 class="modal-title">Modal title</h5>
                        </div>
                        <div class="modal-body">
                        <p>Modal body text goes here.</p>
                        </div>
                        <div class="modal-footer">
                        <button type="button" class="btn btn-primary" onClick={handleClose}>Supratau</button>
                        </div>
                    </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default Home;
