import React, { useState } from 'react';
import Card from '../Components/Card';
import axios from '../api/axios';

const TICKET_URL = '/ticket';

const Home = () => {
    const [data, setData] = useState({
        name: "",
        lastname: "",
        ticketNumber: 0,
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setData({
            ...data,
            [name]: value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const res = await axios.post('http://localhost:8000/api/ticket', data)
        console.log(res);
        // .then((tes) => {console.log(res)})
            
        // try {
        //     const response = axios.post(TICKET_URL,
        //         JSON.stringify(data),
        //         {
        //             headers: {'Content-Type': 'application/json'},
        //             withCredentials: true
        //         }
        //     );
            
        // }catch (err) {

        // }
    }

    return(
        <div className='container d-flex justify-content-center'>
            <div className="card text-center">
                <div className="card-header">
                    Loterijos bilietas
                </div>
                <div className='card-body'>
                <form onSubmit={handleSubmit} method="POST">
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo vardą'
                            name='name'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo pavardę'
                            name='lastname'
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='number' 
                            className='form-control' 
                            placeholder='Įveskite norima skaičių' 
                            name='ticketNumber'
                            onChange={handleChange}
                        />
                    </div>
                    <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Home;
