import React, { useState } from 'react';
import axios from '../api/axios';
import SuccessMessage from '../Components/Modal';

const TICKET_URL = '/ticket';

const Home = () => {
    const [data, setData] = useState({
        name: "",
        lastname: "",
        ticketNumber: 0,
    });

    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');

    const handleChange = (e) => {
        const {name, value} = e.target;
        setData({
            ...data,
            [name]: value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        await axios.post(TICKET_URL, data)
            .then((res) => {
                setPostMessage(res.data.success);
                setShowModal(true);
            })
            .catch((err)=>{console.log(err)})
            console.log(data)
    }

    const handleClose = () => {
        setShowModal(false)
        setData({...data,
            name: "",
            lastname: "",
            ticketNumber: 0,
        })
        console.log(data)
    }

    return(
        <>
        <div className='container d-flex justify-content-center'>
            <div className="card text-center">
                <div className="card-header">
                    Loterijos bilietas
                </div>
                <div className='card-body'>
                <form onSubmit={handleSubmit} method="POST">
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo vardą'
                            name='name'
                            value={data.name}
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='text' 
                            className='form-control' 
                            placeholder='Įveskite savo pavardę'
                            name='lastname'
                            value={data.lastname}
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-group'>
                        <input 
                            type='number' 
                            className='form-control' 
                            placeholder='Įveskite norima skaičių' 
                            name='ticketNumber'
                            value={data.ticketNumber}
                            onChange={handleChange}
                        />
                    </div>
                    <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                    </form>
                </div>
            </div>
        </div>
            {showModal && postMessage && (
                <div class="modal" tabindex="-1" role="dialog">
                    <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                        <h5 class="modal-title">{postMessage}</h5>
                        </div>
                        <div class="modal-body">
                        <p>Jūsų loterijos bilietas numeris: <span className='ticket-color'>#{data.ticketNumber}</span></p>
                        <p>Parodykite arba pasakykite vedėjui savo bilieto numerį, kad jį patvirtintu.</p>
                        <p>Po patvirtinimo prie savo bilieto matysite: <span className='active-color'>"aktyvus"</span></p>
                        </div>
                        <div class="modal-footer">
                        <button type="button" class="btn btn-primary" onClick={handleClose}>Supratau</button>
                        </div>
                    </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default Home;
