import react from 'react';
import { useForm } from 'react-hook-form';

const CancelModal = ({showCancelModal, setShowCancelModal}) => {

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            cancelReason: '',
        }
    });

    const onSubmit = async (data) => {
        await axios.post(`http://localhost:8000/api/admin/cancel`, data)
        .then((res) => {
            console.log(res);
        })
        .catch((err)=>{console.log(err)})
    }


    return(
        <>
            {showCancelModal && (
                <div className='modal'>
                    <div className='modal-dialog'>
                        <div className='modal-content'>
                            <div className='modal-header'>
                                <h5 className='modal-title'>Atšaukti užsakymą</h5>
                            </div>
                            <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                <div className='modal-body'>
                                    <div class="form-group">
                                        <label>Nurodyti priežastį:</label>
                                        <textarea class="form-control" {...register("cancelReason", {required: true})}></textarea>
                                        <div class="invalid-feedback">
                                            {errors.cancelReason?.type === "required" && "Privaloma įvesti priežastį"}
                                        </div>
                                    </div>
                                </div>
                                <div className='modal-footer'>
                                    <button type='submit' class='btn btn-primary'>Patvirtinti</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default CancelModal;
