import React, { useState } from 'react';
import axios from '../api/axios';
import Modal from '../Components/Modal';
import { useForm } from 'react-hook-form';

const TICKET_URL = '/ticket';

const Home = () => {
    // const [data, setData] = useState({
    //     name: "",
    //     lastname: "",
    //     ticketNumber: 0,
    // });

    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
    } = useForm();

    const onSubmit = async (data) => {
        await axios.post(TICKET_URL, data)
            .then((res) => {
                setPostMessage(res.data.success);
                setShowModal(true);
            })
            .catch((err)=>{console.log(err)})
            console.log(data)
    };

    
    // const handleChange = (e) => {
    //     const {name, value} = e.target;
    //     setData({
    //         ...data,
    //         [name]: value
    //     })
    // };

    // const handleSubmit = async (e) => {
    //     e.preventDefault();
    //     const {name, value} = e.target;
    //     console.log(name, value)
    //     validate(name, value)
    //     await axios.post(TICKET_URL, data)
    //         .then((res) => {
    //             setPostMessage(res.data.success);
    //             setShowModal(true);
    //         })
    //         .catch((err)=>{console.log(err)})
    // }

    const handleClose = (data) => {
        setShowModal(false)
        // setData({...data,
        //     name: '', 
        //     lastname: '', 
        //     ticketNumber: 0,
        // })
    }
    
    return(
        <>
            {/* <Modal showModal={showModal} postMessage={postMessage} ticketNumber={data.ticketNumber} handleClose={handleClose}/> */}
            <div className='container d-flex justify-content-center'>
                <div className="card text-center">
                    <div className="card-header">
                        Loterijos bilietas
                    </div>
                    <div className='card-body'>
                    <form onSubmit={handleSubmit(onSubmit)} method="POST">
                        <div className='form-group'>
                            <input 
                                type='text' 
                                className='form-control' 
                                placeholder='Įveskite savo vardą'
                                {...register("name", {required: true})}
                            />
                            <span>
                                {errors.name?.type === "required" && "Vardas yra privalomas"}
                            </span>
                        </div>
                        <div className='form-group'>
                            <input 
                                type='text' 
                                className='form-control' 
                                placeholder='Įveskite savo pavardę'
                                {...register("lastname", {required: true})}
                            />
                            <span>
                                {errors.lastname?.type === "required" && "Pavardė yra privaloma"}
                            </span>
                        </div>
                        <div className='form-group'>
                            <input 
                                type='number' 
                                className='form-control' 
                                placeholder='Įveskite norima skaičių' 
                                {...register("ticketNumber", {min: 1, max: 1000})}
                            />
                            <span>
                                {errors.ticketNumber?.type === 'min' && "Minimalus bilieto skaičius 1"}
                                {errors.ticketNumber?.type === 'max' && "Maksimalus bilieto skaičius 1000"}
                            </span>
                        </div>
                        <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
