import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import { useNavigate } from 'react-router-dom'
import { BsSearch } from "react-icons/bs";
import CancelModal from '../Components/CancelModal';
 

const Other = () => {

    const navigate = useNavigate();
    const sessionToken = sessionStorage.getItem("loggedIn");
    const [data, setData] = useState({});
    const [msg, setMsg] = useState('');
    const [showCancelModal, setShowCancelModal] = useState(false);

    useEffect(() => {
        if(!sessionToken)
        {
            navigate('/login');
        }
        const getData = async () => {
            const {data: res} = await axios.get('http://localhost:8000/api/admin');
            setData(res);
        }
        getData();
    }, [msg])
    
    const handleAccept = async (data) => {
        await axios.post(`http://localhost:8000/api/admin/confirm/${data}`)
        .then((res) => {
            setMsg(res.data.success)
        })
        .catch((err)=>{console.log(err)})
    }

    const handleCancel = () => {
        setShowCancelModal(true);
    }

    const handleInfo = () => {

    }
    return(
        <>
            {sessionToken &&
            <>
                <CancelModal />
                <div className='container d-flex justify-content-center'>
                    <div className="card text-center">
                        <div className="card-header">
                            Admin
                        </div>
                        <div className='card-body'>
                        <div class="input-group">
                            <div class="form-outline">
                                <input  type="search" id="form1" class="form-control" placeholder='search...'/>
                            </div>
                            <button id="search-button" type="button" class="btn btn-primary">
                                <BsSearch />
                            </button>
                        </div>
                            <Table striped bordered>
                                <thead>
                                    <tr>
                                    <th>ID</th>
                                    <th>Name,Lastname</th>
                                    <th>Status</th>
                                    <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.orders?.map((order, index) => (
                                        <tr key={index}>
                                            <td>{order.order_nr}</td>
                                            <td>{order.tickets[0].name} {order.tickets[0].lastname}</td>
                                            <td>{order.active}</td>
                                            <td>
                                                {order.active === 0 ?
                                                    <>
                                                        <button className='btn btn-primary admin-table' onClick={() => handleAccept(order.id)}>Accept</button> 
                                                        <button className='btn btn-primary admin-table' onClick={handleCancel}>Cancel</button>
                                                    </>
                                                    : 
                                                    "Accepted"
                                                }
                                                <button className='btn btn-primary admin-table' onClick={handleInfo}>Info</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                    </div>
                </div>
            </>
            }
        </>
    )
}

export default Other;
