import React, { useState } from 'react';
import axios from '../api/axios';
import Modal from '../Components/Modal';

const TICKET_URL = '/ticket';

const Home = () => {
    const [data, setData] = useState({
        name: "",
        lastname: "",
        ticketNumber: 0,
    });

    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');
    const [error, setError] = useState(false);
    
    const handleChange = (e) => {
        const {name, value} = e.target;
        setData({
            ...data,
            [name]: value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if(data.name.length === 0 || data.lastname.length === 0){
            setError(true);
        }else{
            setError(false);
        }
        // const {name, value} = e.target;
        // console.log(name, value)
        // validate(name, value)
        // await axios.post(TICKET_URL, data)
        //     .then((res) => {
        //         setPostMessage(res.data.success);
        //         setShowModal(true);
        //     })
        //     .catch((err)=>{console.log(err)})
    }

    const handleClose = () => {
        setShowModal(false)
        setData({
            name: "",
            lastname: "",
            ticketNumber: 0,
        })
    }


    // const validate = (name, value) => {
    //     switch (name) {
    //         case 'name':
    //             if(value.length < 5){
    //                 setErrors('Laukelis negali būti tuščias')
    //             }else {
    //                 setErrors('');
    //             }
    //             break;
        
    //         default:
    //             break;
    //     }
    // }

    // console.log(errors)

    return(
        <>
            <Modal showModal={showModal} postMessage={postMessage} ticketNumber={data.ticketNumber} handleClose={handleClose}/>
            <div className='container d-flex justify-content-center'>
                <div className="card text-center">
                    <div className="card-header">
                        Loterijos bilietas
                    </div>
                    <div className='card-body'>
                    <form onSubmit={handleSubmit} method="POST">
                        <div className='form-group'>
                            <input 
                                type='text' 
                                className='form-control' 
                                placeholder='Įveskite savo vardą'
                                name='name'
                                value={data.name}
                                onChange={handleChange}
                            />
                            {error ?
                                <label>Vardas negali būti tuščias</label>
                                :
                                ""
                            }
                        </div>
                        <div className='form-group'>
                            <input 
                                type='text' 
                                className='form-control' 
                                placeholder='Įveskite savo pavardę'
                                name='lastname'
                                value={data.lastname}
                                onChange={handleChange}
                            />
                            {error ?
                                <label>Pavardė negali būti tuščias</label>
                                :
                                ""
                            }
                        </div>
                        <div className='form-group'>
                            <input 
                                type='number' 
                                className='form-control' 
                                placeholder='Įveskite norima skaičių' 
                                name='ticketNumber'
                                value={data.ticketNumber}
                                onChange={handleChange}
                            />
                        </div>
                        <button type='submit' className='btn btn-primary'>Nusipirkti</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
