import React, { useState, useEffect } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import axios from 'axios';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import Dropdown from 'react-bootstrap/Dropdown';
import Table from 'react-bootstrap/Table';



const Profile = (props) => {

    const getToken = localStorage.getItem('userToken');
    
    const TICKET_URL = `http://localhost:8000/api/ticket-list/${getToken}`;
    
    const [data, setData] = useState({});

    const { promiseInProgress } = usePromiseTracker({area: props.area});

    useEffect(() => {
        const getData = async () => {
            if(getToken)
            {
                const {data: res} = await axios.get(TICKET_URL)
                setData(res);
            }
        }
        trackPromise(getData());
    }, [])

    return(
        <>
            {promiseInProgress &&
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {!promiseInProgress &&
                <div className='container d-flex profile justify-content-between'>
                    {console.log(data.orders)}
                    {data.orders?.map((order) => (
                        <>
                            <div className="card card-ticket text-center">
                                    <div className="card-header">
                                        Užsakymas #5151
                                    </div>
                                    <div className='card-body'>
                                    <Dropdown className='w-100'>
                                        <Dropdown.Toggle variant="success" id="dropdown-basic" className='w-100 d-flex justify-content-between align-items-center'>
                                        Bilietų kiekis: {order.quantity}
                                        </Dropdown.Toggle>

                                        <Dropdown.Menu>
                                            <div className='dropdown-body'>
                                                <div className='tickets d-flex w-100 justify-content-between'>
                                                <Table striped bordered hover>
                                                <thead>
                                                    <tr>
                                                        <th>#</th>
                                                        <th>Vardas</th>
                                                        <th>Pavarde</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    <tr>
                                                    <td>1</td>
                                                    <td>Mark</td>
                                                    <td>Otto</td>
                                                    <td>@mdo</td>
                                                    </tr>
                                                    <tr>
                                                    <td>2</td>
                                                    <td>Jacob</td>
                                                    <td>Thornton</td>
                                                    <td>@fat</td>
                                                    </tr>
                                                </tbody>
                                                </Table>
                                                    <div>Numeris:</div>
                                                    <div>#4545</div>
                                                    <div>Vardas: belekasbelekaip</div>
                                                    <div>Pavarde: belekaipbelekaip</div>
                                                </div>
                                            </div>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                    </div>
                                </div>
                        </>
                    //     <div className="card card-ticket text-center">
                    //     <div className="card-header">
                    //         Jusu orderis
                    //     </div>
                    //     <div className='card-body'>
                    //         <p>Nusipirkote: {order.quantity} bilietų</p>
                    //     </div>
                    // </div>
                    ))}
                    {/* {tickets.tickets?.map((ticket) => (
                                <div className="card card-ticket text-center" key={ticket.id}>
                                    <div className="card-header">
                                        Loterijos bilietas #{ticket.ticketNumber}
                                    </div>
                                    <div className='card-body'>
                                        <p>{ticket.name}</p>
                                        <p>{ticket.lastname}</p>
                                    </div>
                                </div>
                    ))}
                    {tickets.tickets?.length === 0 && (
                            <div className="card card-ticket text-center">
                                <div className="card-header">
                                    Informacija
                                </div>
                                <div className='card-body'>
                                    <p>Šiuo metu neturite įsigyje loterijos bilieto</p>
                                </div>
                            </div>
                    )} */}
                </div>
            }
        </>
    )
}

export default Profile;
