import React, { useState, useEffect } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import axios from 'axios';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import Dropdown from 'react-bootstrap/Dropdown';



const Profile = (props) => {

    const getToken = localStorage.getItem('userToken');
    
    const TICKET_URL = `http://localhost:8000/api/ticket-list/${getToken}`;
    
    const [data, setData] = useState({});

    const { promiseInProgress } = usePromiseTracker({area: props.area});

    useEffect(() => {
        const getData = async () => {
            if(getToken)
            {
                const {data: res} = await axios.get(TICKET_URL)
                setData(res);
            }
        }
        trackPromise(getData());
    }, [])

    return(
        <>
            {promiseInProgress &&
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {!promiseInProgress &&
                <div className='container d-flex profile justify-content-between'>
                    {console.log(data.orders)}
                    {data.orders?.map((order) => (
                        <>
                            <Dropdown className='card-ticket w-100'>
                                <Dropdown.Toggle variant="success" id="dropdown-basic" className='w-100 d-flex justify-content-between align-items-center'>
                                Bilietų kiekis: {order.quantity}
                                </Dropdown.Toggle>

                                <Dropdown.Menu>
                                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                            <div className="card card-ticket text-center">
                                    <div className="card-header">
                                        Loterijos bilietas #
                                    </div>
                                    <div className='card-body'>
                                        <p>asdasdasdas</p>
                                        <p>asdasd</p>
                                    </div>
                                </div>
                        </>
                    //     <div className="card card-ticket text-center">
                    //     <div className="card-header">
                    //         Jusu orderis
                    //     </div>
                    //     <div className='card-body'>
                    //         <p>Nusipirkote: {order.quantity} bilietų</p>
                    //     </div>
                    // </div>
                    ))}
                    {/* {tickets.tickets?.map((ticket) => (
                                <div className="card card-ticket text-center" key={ticket.id}>
                                    <div className="card-header">
                                        Loterijos bilietas #{ticket.ticketNumber}
                                    </div>
                                    <div className='card-body'>
                                        <p>{ticket.name}</p>
                                        <p>{ticket.lastname}</p>
                                    </div>
                                </div>
                    ))}
                    {tickets.tickets?.length === 0 && (
                            <div className="card card-ticket text-center">
                                <div className="card-header">
                                    Informacija
                                </div>
                                <div className='card-body'>
                                    <p>Šiuo metu neturite įsigyje loterijos bilieto</p>
                                </div>
                            </div>
                    )} */}
                </div>
            }
        </>
    )
}

export default Profile;
