import React from 'react';
import cookie from 'react-cookies';


const Profile = () => {

    const handleClick = () => {
        const expires = new Date();
        expires.setDate(Date.now() + 1000 * 60 * 60 * 24 * 14);

        cookie.save(
            'cookieName',
            '123',
            {
                path: '/',
                expires,
                maxAge: 1000,
                domain: 'http://localhost:3000',
                secure: true,
                httpOnly: false,
            }
        )
    }

    return(
        <button type='submit' onClick={handleClick} className='btn btn-primary'>Set cookie</button>
    )
}

export default Profile;
