import react from 'react';
import { useForm } from 'react-hook-form';

const CancelModal = ({showCancelModal, setShowCancelModal}) => {

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            cancelReason: '',
        }
    });

    const onSubmit = (data) => {
        console.log('pressed')
        console.log(data.cancelReason);
    }
    // const handleClose = async (e) => {
    //     e.preventDefault();

    //     const value = e.target.value;
    //     // await axios.post(`http://localhost:8000/api/admin/confirm/${data}`)
    //     // .then(() => {
    //     //     setShowCancelModal(false);
    //     // })
    //     // .catch((err)=>{console.log(err)})
    // }

    return(
        <>
            {showCancelModal && (
                <div className='modal'>
                    <div className='modal-dialog'>
                        <div className='modal-content'>
                            <div className='modal-header'>
                                <h5 className='modal-title'>Atšaukti užsakymą</h5>
                            </div>
                            <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                <div className='modal-body'>
                                    <div class="form-group">
                                        <label htmlFor="cancelReason">Nurodyti priežastį:</label>
                                        <textarea class="form-control" id="cancelReason" {...register("cancelReason", {required: true})}></textarea>
                                        <div class="invalid-feedback">
                                            {errors.cancelReason?.type === "required" && "Privaloma įvesti priežastį"}
                                        </div>
                                    </div>
                                </div>
                                <div className='modal-footer'>
                                    <button type='button' class='btn btn-primary'>Patvirtinti</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}

export default CancelModal;
